BS_call <- function (S0,K,T,r,sigma){
  d1 <- (log(S0/K)+(r+0.5*sigma^2)*T)/(sigma*sqrt(T)) 
  d2 <- d1- sigma*sqrt(T)
  opt.val <- -S0*pnorm(-d1)+K*exp(-r*T)*pnorm(-d2) 
  return(opt.val)
}

